# coding = utf-8

import base64
import numpy as np
from PIL import Image
from io import BytesIO

import tensorflow as tf

from logger import log
from ml.captcha_crack_baidu.train import crack_captcha
from ml.captcha_crack_baidu.train import convert2gray
from ml.captcha_crack_baidu.train import crack_captcha_cnn


class Crack:
    output = crack_captcha_cnn()
    saver = tf.train.Saver()

    def predict(self, base64_img_str):
        """
        验证码预测（识别）
        :param base64_img_str: base64格式的图片图像
        :return: 识别出的结果字符
        """
        image = Image.open(BytesIO(base64.b64decode(base64_img_str)))
        image_data = np.array(image)
        image_data = convert2gray(image_data)
        image_data = image_data.flatten() / 255
        predict_text = crack_captcha(image_data, self.output, self.saver)

        return predict_text


if __name__ == '__main__':
    crack = Crack()
    img_str = r''
    text = crack.predict(img_str)
    log("正确值为：JNWx, 预测值为：{0}".format(text))  # JNWx
    pass
